package com.loop;

public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println("The Natural Numbers Are:");

//it will first at least once execute then checks the condition of the remaining program 
//but while loop first checks the condition then executes the program

		int a = 46; 
		do {
			System.out.println(a);
			a++ ;
		}while(a<45);
		
		
	}

}
